import { motion } from "framer-motion";
import { useEffect, useState } from "react";

export default function SpiralMotion() {
  const [points, setPoints] = useState([]);
  const totalSteps = 200; // Number of points in the spiral
  const speed = 0.05; // Speed of movement

  useEffect(() => {
    let newPoints = [];
    for (let i = 0; i < totalSteps; i++) {
      const angle = i * 0.2; // Spiral angle increment
      const radius = 5 * Math.sqrt(i); // Spiral radius
      const x = radius * Math.cos(angle);
      const y = radius * Math.sin(angle);
      newPoints.push({ x, y });
    }
    setPoints(newPoints);
  }, []);

  return (
    <div className="flex items-center justify-center h-screen bg-gray-900">
      <motion.div
        className="w-10 h-10 bg-blue-500 rounded-full absolute"
        animate={{
          x: points.map((p) => p.x),
          y: points.map((p) => p.y),
        }}
        transition={{
          duration: totalSteps * speed,
          ease: "linear",
          repeat: Infinity,
        }}
      />
    </div>
  );
}
